create table sc_user
(
    id         bigint generated by default as identity
        constraint pk_sc_user primary key,
    user_name  varchar(255),
    first_name varchar(255),
    last_name  varchar(255),
    e_mail     varchar(255)
        constraint uc_sc_user_e_mail
            unique
);

alter table sc_user
    owner to admin;


